mapper.select:
SELECT COMMENT_NO, BOARD_NO, COMMENT_CONTENT, TO_CHAR(REGDATE, 'MM-DD HH24:MI') AS REGDATE, NICKNAME FROM
DIARY_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL INTRO_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL KNOWHOW_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL MISSING_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL USED_TRANSACTION_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL EVENT_COMMENTS C, USER_ACCOUNT U WHERE C.MEMBER_SERIAL = U.MEMBER_SERIAL
AND BOARD_NO = ${board_no}

mapper.delete:
DELETE FROM
DIARY_BOARD INTRO_BOARD KNOWHOW_BOARD MISSING_BOARD USED_TRANSACTION_BOARD EVENT_BOARD
WHERE BOARD_NO = #{board_no}
