SELECT * FROM 
	(
		SELECT ROWNUM AS RN, PROD.* FROM 
		( 
			SELECT 
				P_ID, SUBSTR(P_NAME, 0, 17) AS P_NAME, P_TYPE, P_CATEGORY, 
  				PRICE, STOCK, S_IMAGE, L_IMAGE 
  			FROM STORE_PRODUCTS
 			WHERE 1=1

 			  AND P_TYPE = #{idx}


 			  AND P_CATEGORY = #{keyword}

 			ORDER BY P_ID DESC 
 		) PROD 
 	)
 WHERE RN BETWEEN #{begin} AND #{end}

SELECT 	COUNT(*) AS CNT 
FROM	STORE_PRODUCTS
WHERE	1=1

 			  AND P_TYPE = #{idx}


 			  AND P_CATEGORY = #{keyword}

SELECT P_ID, P_NAME, P_TYPE, P_CATEGORY, PRICE, STOCK, S_IMAGE, L_IMAGE
  FROM STORE_PRODUCTS
 WHERE P_ID = #{p_id}
