mapper.update:
UPDATE STORE_ORDER 
SET AMOUNT = #{AMOUNT}, MEMO = #{MEMO}, 
	ADDRESS = #{address}, ADDRESS_DETAIL = #{address_detail}, ZIP = #{zip} 
WHERE ORDER_ID = #{ORDER_ID}

mapper.select:
SELECT * FROM STORE_ORDER
WHERE MEMBER_SERIAL = #{member_serial}
ORDER BY ORDER_ID DESC

mapper.update:
UPDATE STORE_PRODUCT
SET STOCK = STOCK - ${stock}
WHERE P_ID = #{p_id}
